#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="C:\Users\pt\Desktop\item\create-template\node_modules\.pnpm\m@1.9.0\node_modules\m\bin\node_modules;C:\Users\pt\Desktop\item\create-template\node_modules\.pnpm\m@1.9.0\node_modules\m\node_modules;C:\Users\pt\Desktop\item\create-template\node_modules\.pnpm\m@1.9.0\node_modules;C:\Users\pt\Desktop\item\create-template\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/c/Users/pt/Desktop/item/create-template/node_modules/.pnpm/m@1.9.0/node_modules/m/bin/node_modules:/mnt/c/Users/pt/Desktop/item/create-template/node_modules/.pnpm/m@1.9.0/node_modules/m/node_modules:/mnt/c/Users/pt/Desktop/item/create-template/node_modules/.pnpm/m@1.9.0/node_modules:/mnt/c/Users/pt/Desktop/item/create-template/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/bash$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/bash$exe"  "$basedir/../../bin/m" $args
  } else {
    & "$basedir/bash$exe"  "$basedir/../../bin/m" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "bash$exe"  "$basedir/../../bin/m" $args
  } else {
    & "bash$exe"  "$basedir/../../bin/m" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
